import React, { useState } from "react";

// Seed data
const vegetables = [
  { id: 4, name: "Potato", price: 5 },
  { id: 5, name: "Onion", price: 3 },
  { id: 6, name: "Brinjal", price: 4 },
];

const vegetablesCategory = () => {
  const [cart, setCart] = useState([]);

  // Add to cart or update quantity
  const updateCart = (vegetable, action) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find((item) => item.id === vegetable.id);

      if (action === "add") {
        if (existingItem) {
          return prevCart.map((item) =>
            item.id === vegetable.id
              ? { ...item, quantity: item.quantity + 1 }
              : item
          );
        } else {
          return [...prevCart, { ...vegetable, quantity: 1 }];
        }
      }

      if (action === "remove") {
        return prevCart.filter((item) => item.id !== vegetable.id);
      }

      return prevCart;
    });
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Available Vegetables</h2>
      {vegetables.map((vegetable) => (
        <div key={vegetable.id} style={{ marginBottom: "10px" }}>
          <h4>{vegetable.name} - ${vegetable.price}</h4>
          <button onClick={() => updateCart(vegetable, "add")}>Add to Cart</button>
        </div>
      ))}

      {cart.length > 0 && (
        <div style={{ marginTop: "30px" }}>
          <h3>Your Cart</h3>
          {cart.map((item) => (
            <div key={item.id} style={{ marginBottom: "8px" }}>
              {item.name} x {item.quantity} â€” ${item.price * item.quantity}
              <button
                style={{ marginLeft: "10px" }}
                onClick={() => updateCart(item, "remove")}
              >
                Remove
              </button>
            </div>
          ))}
          <h4>Total: ${total}</h4>
        </div>
      )}
    </div>
  );
};

export default vegetablesCategory;
