import React, { useState } from "react";

const seedData = [
  { id: 1, name: "Watermelon Seeds", price: 5, img: "/watermelon.jpg" },
  { id: 2, name: "Tomato Seeds", price: 3, img: "/tomato.jpg" },
  { id: 3, name: "Carrot Seeds", price: 4, img: "/carrot.jpg" },
];

const Seedspage = () => {
  const [cart, setCart] = useState([]);

  const handleCartAction = (seed, action) => {
    setCart((prevCart) => {
      const item = prevCart.find((item) => item.id === seed.id);
      if (action === "add") {
        return item ? prevCart.map((item) => (item.id === seed.id ? { ...item, quantity: item.quantity + 1 } : item)) : [...prevCart, { ...seed, quantity: 1 }];
      } else if (action === "remove") {
        return prevCart.filter((item) => item.id !== seed.id);
      }
      return prevCart;
    });
  };

  const calculateTotal = () => cart.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2>Seeds Cart</h2>
      <h3>Available Seeds</h3>
      <div>
        {seedData.map((seed) => (
          <div key={seed.id} style={{ margin: '10px', display: 'inline-block', textAlign: 'center' }}>
            <h4>{seed.name}</h4>
            <img src={seed.img} alt={seed.name} style={{ width: '150px', borderRadius: '10px' }} />
            <p>${seed.price}</p>
            <button onClick={() => handleCartAction(seed, "add")} style={{ padding: '5px 10px' }}>Add to Cart</button>
          </div>
        ))}
      </div>
      <h3>Your Cart</h3>
      {cart.length === 0 ? <p>Cart is empty</p> : cart.map((item) => (
        <div key={item.id} style={{ display: 'flex', justifyContent: 'center', margin: '10px' }}>
          <img src={item.img} alt={item.name} style={{ width: '80px', borderRadius: '10px' }} />
          <div>
            <p>{item.name} x {item.quantity} - ${item.price * item.quantity}</p>
            <button onClick={() => handleCartAction(item, "remove")} style={{ padding: '5px 10px' }}>Remove</button>
          </div>
        </div>
      ))}
      {cart.length > 0 && (
        <div>
          <h4>Total: ${calculateTotal()}</h4>
          <button style={{ padding: '10px 20px', backgroundColor: 'lightblue' }}>Checkout</button>
        </div>
      )}
    </div>
  );
};

export default Seedspage;
