import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Seedspage() {
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);

  const seeds = [
    { id: 1, name: "Watermelon Seeds", price: 5 },
    { id: 2, name: "Tomato Seeds", price: 3 },
    { id: 3, name: "Carrot Seeds", price: 4 },
  ];

  const updateCart = (seed, action) => {
    setCart((prev) => {
      const existingSeed = prev.find((item) => item.id === seed.id);
      if (action === "add") {
        return existingSeed
          ? prev.map((item) =>
              item.id === seed.id ? { ...item, quantity: item.quantity + 1 } : item
            )
          : [...prev, { ...seed, quantity: 1 }];
      }
      return prev.filter((item) => item.id !== seed.id);
    });
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handlePayment = async () => {
    try {
      await axios.post("http://localhost:5000/api/orders", {
        total_amount: total,
        total_items: cart.length,
      });
      navigate("/PaymentPage");
    } catch (error) {
      console.error("Payment failed:", error);
      alert("Failed to process payment.");
    }
  };

  return (
    <div className="page-container">
      <h2>Available Seeds</h2>
      {seeds.map((seed) => (
        <div key={seed.id} className="seed-item">
          <h4>{seed.name} - ${seed.price}</h4>
          <button onClick={() => updateCart(seed, "add")}>Add to Cart</button>
        </div>
      ))}
      {cart.length > 0 && (
        <div className="cart">
          <h3>Your Cart</h3>
          {cart.map((item) => (
            <div key={item.id} className="cart-item">
              {item.name} x {item.quantity} â€” ${item.price * item.quantity}
              <button onClick={() => updateCart(item, "remove")}>Remove</button>
            </div>
          ))}
          <h4>Total: ${total}</h4>
          <button onClick={handlePayment} className="payment-btn">
            Confirm Payment
          </button>
        </div>
      )}
    </div>
  );
}

export default Seedspage;
