import React from 'react';
import { useNavigate } from 'react-router-dom';

function HomePage() {
  const navigate = useNavigate();

  // Handle login navigation
  const handleLoginClick = () => {
    navigate('/loginpage');
  };

  return (
    <div style={{ backgroundColor: '#dff0d8', minHeight: '100vh', fontFamily: 'Arial, sans-serif' }}>
      <div style={{ textAlign: 'center', paddingTop: '50px' }}>
        <h1 style={{ fontSize: '3rem', color: '#388E3C' }}>E-farming</h1>
        <p style={{ fontSize: '1.2rem', color: '#333' }}>Welcome to the future of farming!</p>

        {/* Navigation Links */}
        <nav style={{ margin: '20px 0' }}>
          <a href="info.html" style={linkStyle}>Information</a>
          <a href="about.html" style={linkStyle}>About Us</a>
          <a href="contact.html" style={linkStyle}>Contact Us</a>
        </nav>

        {/* Main Image */}
        <div style={{ marginBottom: '20px' }}>
          <img
            src="Homepage.jpg"
            alt="Homepage"
            style={{ width: '80%', maxWidth: '400px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}
          />
        </div>

        {/* Login Button */}
        <button
          onClick={handleLoginClick}
          style={{
            padding: '12px 30px',
            fontSize: '1.2rem',
            borderRadius: '30px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            cursor: 'pointer',
            transition: 'background-color 0.3s',
          }}
          onMouseOver={(e) => e.target.style.backgroundColor = '#388E3C'}
          onMouseOut={(e) => e.target.style.backgroundColor = '#4CAF50'}
        >
          Go to Login
        </button>

        {/* Second Image */}
        <div style={{ marginTop: '30px' }}>
          <img
            src="hompage2.jpg"
            alt="Homepage 2"
            style={{ width: '80%', maxWidth: '400px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}
          />
        </div>
      </div>
    </div>
  );
}

const linkStyle = {
  fontSize: '1.1rem',
  color: '#388E3C',
  margin: '0 15px',
  textDecoration: 'none',
  transition: 'color 0.3s',
};

export default HomePage;
