import React, { useState } from "react";

// Sample seed data with prices
const seeds = [
  { id: 1, name: "Watermelon Seeds", price: 5, img: "/watermelon.jpg" },
  { id: 2, name: "Tomato Seeds", price: 3, img: "/tomato.jpg" },
  { id: 3, name: "Carrot Seeds", price: 4, img: "/carrot.jpg" },
];

const Seedspage = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (seed) => {
    setCart((prevCart) => {
      const existingSeed = prevCart.find((item) => item.id === seed.id);
      if (existingSeed) {
        return prevCart.map((item) =>
          item.id === seed.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...seed, quantity: 1 }];
      }
    });
  };

  const removeFromCart = (id) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== id));
  };

  const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2>Available Seeds</h2>
      <div>
        {seeds.map((seed) => (
          <div key={seed.id} style={{ display: 'inline-block', margin: '10px' }}>
            <h4>{seed.name}</h4>
            <img src={seed.img} alt={seed.name} style={{ width: '150px' }} />
            <p>${seed.price}</p>
            <button onClick={() => addToCart(seed)}>Add to Cart</button>
          </div>
        ))}
      </div>

      <h3>Your Cart</h3>
      {cart.length === 0 ? (
        <p>Cart is empty</p>
      ) : (
        cart.map((item) => (
          <div key={item.id} style={{ marginBottom: '10px' }}>
            <p>{item.name} x {item.quantity} - ${item.price * item.quantity}</p>
            <button onClick={() => removeFromCart(item.id)}>Remove</button>
          </div>
        ))
      )}

      {cart.length > 0 && (
        <div>
          <h4>Total: ${totalPrice}</h4>
          <button>Checkout</button>
        </div>
      )}
    </div>
  );
};

export default Seedspage;
