import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const PaymentPage = () => {
  const [orderDetails, setOrderDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();
  const { orderId } = useParams(); // Dynamically get orderId from URL

  useEffect(() => {
    // Fetch order details based on the orderId from the URL
    const fetchOrderDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/orders/${orderId}`);
        setOrderDetails(response.data);
        setLoading(false);
      } catch (error) {
        setError('Failed to load order details');
        setLoading(false);
      }
    };

    if (orderId) {
      fetchOrderDetails();
    } else {
      setError('Order ID not found');
      setLoading(false);
    }
  }, [orderId]); // Run effect only when orderId changes

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  // Ensure orderDetails and items are defined before rendering
  const { items = [], total_amount = 0, total_items = 0 } = orderDetails || {};

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Payment Details</h2>

      <h3>Order Summary</h3>
      <p>Total Items: {total_items}</p>
      <p>Total Amount: ${total_amount}</p>

      <h4>Items in the Order:</h4>
      {items.length > 0 ? (
        <ul>
          {items.map((item) => (
            <li key={item.id}>
              {item.name} x {item.quantity} â€” ${item.price * item.quantity}
            </li>
          ))}
        </ul>
      ) : (
        <p>No items in this order.</p>
      )}

      <br />
      <button
        onClick={() => navigate("/confirmation")}
        style={{
          border: "2px solid black",
          borderRadius: "30px",
          width: "100px",
          height: "50px",
          backgroundColor: "lightgreen",
        }}
      >
        Confirm Payment
      </button>
    </div>
  );
};

export default PaymentPage;
