import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const PaymentPage = () => {
  const [orderDetails, setOrderDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    // Assuming the order ID is passed as a route parameter
    const orderId = 1; // Replace with dynamic order ID if necessary

    // Fetch order details
    const fetchOrderDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/orders/${orderId}`);
        setOrderDetails(response.data);
        setLoading(false);
      } catch (error) {
        setError('Failed to load order details');
        setLoading(false);
      }
    };

    fetchOrderDetails();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Payment Details</h2>

      <h3>Order Summary</h3>
      <p>Total Items: {orderDetails.total_items}</p>
      <p>Total Amount: ${orderDetails.total_amount}</p>

      <h4>Items in the Order:</h4>
      <ul>
        {orderDetails.items.map((item) => (
          <li key={item.id}>
            {item.name} x {item.quantity} â€” ${item.price * item.quantity}
          </li>
        ))}
      </ul>

      <br />
      <button
        onClick={() => navigate("/confirmation")}
        style={{
          border: "2px solid black",
          borderRadius: "30px",
          width: "100px",
          height: "50px",
          backgroundColor: "lightgreen",
        }}
      >
        Confirm Payment
      </button>
    </div>
  );
};

export default PaymentPage;
