import React from 'react';
import { useNavigate, Link } from 'react-router-dom';

function HomePage() {
  const navigate = useNavigate();

  const handleLoginClick = () => {
    navigate('/loginpage'); // Programmatic navigation to /loginpage
  };

  return (
    <div style={{ backgroundColor: 'lightgreen', minHeight: '100vh' }}>
      <center>
        <h1>E-farming</h1>
        <h3>
          {/* Using React Router <Link> instead of <a> */}
          <Link to="/info" style={{ margin: '0 15px', textDecoration: 'none', color: 'black' }}>Information</Link>
          <Link to="/about" style={{ margin: '0 15px', textDecoration: 'none', color: 'black' }}>About us</Link>
          <Link to="/contact" style={{ margin: '0 15px', textDecoration: 'none', color: 'black' }}>Contact us</Link>
        </h3>

        <br />
        {/* Images (if inside public/ folder, use / at start) */}
        <img src="/Homepage.jpg" alt="Homepage" height="400" width="400" />
        
        <br /><br />

        {/* Button that navigates to login page */}
        <button 
          onClick={handleLoginClick} 
          style={{ padding: '10px 20px', borderRadius: '10px', backgroundColor: 'white', cursor: 'pointer' }}
        >
          Go to Login
        </button>

        <br /><br />

        <img src="/hompage2.jpg" alt="Homepage 2" height="400" width="400" />
      </center>
    </div>
  );
}

export default HomePage;
