import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Seedspage() {
  const navigate = useNavigate();
  const goToPayment = () => {
    navigate("/PaymentPage");
  };

  const [cart, setCart] = useState([]);

  // Seed data
  const seeds = [
    { id: 1, name: "Watermelon Seeds", price: 5 },
    { id: 2, name: "Tomato Seeds", price: 3 },
    { id: 3, name: "Carrot Seeds", price: 4 },
  ];

  // Add to cart or update quantity
  const updateCart = (seed, action) => {
    setCart((prev) => {
      const existingSeed = prev.find((item) => item.id === seed.id);

      if (action === "add") {
        if (existingSeed) {
          return prev.map((item) =>
            item.id === seed.id ? { ...item, quantity: item.quantity + 1 } : item
          );
        } else {
          return [...prev, { ...seed, quantity: 1 }];
        }
      }

      return prev.filter((item) => item.id !== seed.id); // Remove seed
    });
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);

  const handlePayment = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/orders", {
        total_amount: total,
        total_items: totalItems,
      });
      console.log("Order saved:", response.data);
      goToPayment(); // Navigate to PaymentPage
    } catch (error) {
      console.error("Payment failed:", error);
      alert("Failed to process payment.");
    }
  };

  return (
    <div className="seedspage-container">
      <h2>Available Seeds</h2>
      <div className="seeds-list">
        {seeds.map((seed) => (
          <div className="seed-item" key={seed.id}>
            <h4>{seed.name} - ${seed.price}</h4>
            <button className="add-to-cart-btn" onClick={() => updateCart(seed, "add")}>Add to Cart</button>
          </div>
        ))}
      </div>

      {cart.length > 0 && (
        <div className="cart-summary">
          <h3>Your Cart</h3>
          <div className="cart-items">
            {cart.map((item) => (
              <div className="cart-item" key={item.id}>
                {item.name} x {item.quantity} - ${item.price * item.quantity}
                <button
                  className="remove-btn"
                  onClick={() => updateCart(item, "remove")}
                >
                  Remove
                </button>
              </div>
            ))}
          </div>
          <h4>Total: ${total}</h4>
          <button className="payment-btn" onClick={handlePayment}>Confirm Payment</button>
        </div>
      )}
    </div>
  );
}

export default Seedspage;
