import React, { useState } from "react";

// Sample seed data (with prices)
const seedData = [
  { id: 1, name: "Watermelon Seeds", price: 5, img: "/watermelon.jpg" },
  { id: 2, name: "Tomato Seeds", price: 3, img: "/tomato.jpg" },
  { id: 3, name: "Carrot Seeds", price: 4, img: "/carrot.jpg" },
];

const Seedspage = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (seed) => {
    setCart((prevCart) => {
     
      const existingItem = prevCart.find(item => item.id === seed.id);
      if (existingItem) {
       
        return prevCart.map(item =>
          item.id === seed.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        
        return [...prevCart, { ...seed, quantity: 1 }];
      }
    });
  };


  const removeFromCart = (id) => {
    setCart((prevCart) => prevCart.filter(item => item.id !== id));
  };

  
  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2>Seeds Cart</h2>
      <h3>Available Seeds</h3>

      <div>
        {seedData.map((seed) => (
          <div key={seed.id} style={{ marginBottom: '20px', display: 'inline-block', padding: '10px' }}>
            <h4>{seed.name}</h4>
            <img 
              src={seed.img} 
              alt={seed.name} 
              style={{ width: '200px', height: 'auto', borderRadius: '10px' }} 
            />
            <p>Price: ${seed.price}</p>
            <button 
              onClick={() => addToCart(seed)} 
              style={{ padding: '10px 20px', backgroundColor: 'lightgreen', borderRadius: '10px' }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {/* Cart Display */}
      <div style={{ marginTop: '50px' }}>
        <h3>Your Cart</h3>
        {cart.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          cart.map((item) => (
            <div key={item.id} style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
              <img 
                src={item.img} 
                alt={item.name} 
                style={{ width: '100px', height: 'auto', borderRadius: '10px', marginRight: '10px' }}
              />
              <div>
                <p>{item.name} - ${item.price} x {item.quantity}</p>
                <button
                  onClick={() => removeFromCart(item.id)}
                  style={{ padding: '5px 10px', backgroundColor: 'lightcoral', borderRadius: '10px' }}
                >
                  Remove
                </button>
              </div>
            </div>
          ))
        )}

        {/* Total Price */}
        {cart.length > 0 && (
          <div>
            <h4>Total: ${calculateTotal()}</h4>
            <button style={{ padding: '10px 20px', backgroundColor: 'lightblue', borderRadius: '10px' }}>
              Checkout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Seedspage;
